$(document).ready(function(){$(".slider-partner").slick({slidesToShow:4,slidesToScroll:4,speed:500,dots:!0,arrows:!1,rows:0,responsive:[{breakpoint:1200,settings:{arrows:!1,centerMode:!0,centerPadding:"40px",slidesToShow:3,slidesToScroll:3}},{breakpoint:480,settings:{arrows:!1,centerMode:!1,slidesToShow:2,slidesToScroll:2}}],customPaging:function(s,e){return'<span class="dot"></span>'}}),$(".slider-control--right").click(function(){$(this).closest(".slider-wrap").find(".slider-item").slick("slickNext")}),$(".slider-control--left").click(function(){$(this).closest(".slider-wrap").find(".slider-item").slick("slickPrev")})});